import React, { useState } from "react";

const FileUploader = () => {
	const [selectedFile, setSelectedFile] = useState(null);

	const handleFileChange = (event) => {
		const file = event.target.files[0];
		setSelectedFile(file);
	};

	const handleFileUpload = () => {
		if (!selectedFile) {
			console.log("No se ha seleccionado ningún archivo.");
			return;
		}

		const reader = new FileReader();
		reader.onload = (event) => {
			const fileContent = event.target.result;

			// Pass the file content to your desordenar script
			callDesordenarScript(fileContent);
		};
		reader.readAsText(selectedFile);
	};

	const callDesordenarScript = (fileContent) => {
		// Place your desordenar script logic here, using the file content
		console.log("File content:", fileContent);
		// ... your code here ...
	};

	return (
		<div>
			<input type="file" onChange={handleFileChange} />
			<button onClick={handleFileUpload}>
				Desordenar Preguntas y Respuestas
			</button>
		</div>
	);
};

export default FileUploader;
